(function ($)
{

    // Public: jScroll Plugin
    $.fn.jScroll = function (options)
    {

        var opts = $.extend({}, $.fn.jScroll.defaults, options);

        return this.each(function ()
        {
            var $element = $(this);
            var $window = $(window);
            var locator = new location($element);

            var scrollFunction = function ()
            {
                if ($element.parent().height() < window.innerHeight)
                {
                    $element.removeClass('autoScrollOff');
                    $element.addClass('autoScrollOn');
                    return;
                }
                //$element.css('margin-top', '0px');
                $element.removeClass('autoScrollOn');
                $element.addClass('autoScrollOff');
            };

            $window.load(scrollFunction);
            $window.scroll(scrollFunction);
            $window.resize(scrollFunction);
        });

    };

    // Public: Default values
    $.fn.jScroll.defaults = {
        speed: "fast",
        top: 10
    };

})(jQuery);